mapper.select:
Select * From ( Select rownum rn, a.* From ( Select b.*, m.m_nickname From Board b, Member m Where b.b_re_level = 0 and b.member_id = m.member_id
order by b.b_view_cnt desc
order by b.b_like_cnt desc
order by b.create_date desc
) a ) Where rn between #{start} and #{end}

Select * From ( Select rownum rn, a.* From ( Select b.*, m.m_nickname From Board b, Member m Where b.b_re_level = 0 and b.member_id = m.member_id and b.b_common_board = #{b_common_board}
order by b.b_view_cnt desc
order by b.b_like_cnt desc
order by b.create_date desc
) a ) Where rn between #{start} and #{end}

mapper.update:
Update   Board
Set      b_view_cnt = b_view_cnt + 1
Where   board_id = #{board_id}

mapper.delete:
 Delete  From Board
 Where
 
    
       board_id = #{board_id}
    
    
       b_ref = #{board_id}

mapper.select:
{
   call boardDelRe(
       #{b_ref,      mode=IN,   jdbcType=INTEGER}
      ,#{b_re_step,   mode=IN,   jdbcType=INTEGER}
      ,#{b_re_level,   mode=IN,   jdbcType=INTEGER}
   )
}
